mapper.select:
SELECT * FROM(
    SELECT ROWNUM RN,TEMP.* FROM(
		SELECT * FROM JCPD_QDHFB
		WHERE LXCODE=#{lxCode} AND szhh>=#{szhh} and #{ezhh}>szhh AND BMCODE=#{bmCode} AND BBID=#{bbid}
		ORDER BY SZHH
	) TEMP
	
	  	where ROWNUM <= (#{page} * #{rows})
	)

   
      RN>((#{page} - 1) * #{rows})

mapper.update:
UPDATE JCPD_BBKZB
SET QMBBID=#{pdbbid}
WHERE BBID=#{bbid}

mapper.select:
SELECT L.*,#{bbid} AS BBID FROM HTGL_LDB L
WHERE BMCODE=#{bmCode}
ORDER BY LXCODE

SELECT * FROM JCPD_QYHFB
WHERE LXCODE=#{lxCode} AND SZHH>=#{szhh} AND SZHH<#{ezhh} AND BBID=#{bbid}
ORDER BY SZHH
