where.if:
  			and  to_number(case
		       when instr(pos,'-') > '0' then substr(pos,0,instr(pos,'-')-1)
		       else pos 
		       end)
		 >= #{startzh}

  			and  to_number(case
			       when instr(pos,'-') > '0' then substr(pos,0,instr(pos,'-')-1)
			       else pos 
			       end)
			 <= #{endzh}

mapper.select:
SELECT * FROM(SELECT ROWNUM RN,TEMP.* FROM( SELECT * FROM LUMIANBX
) TEMP where ROWNUM <= (#{page} * #{rows}))
RN>((#{page} - 1) * #{rows})
