mapper.update:
UPDATE JCPD_QDHFSHB
SET SQZT=1,SHZT=0,SQR=#{sqr},SQSJ=current_date
WHERE BMCODE=#{bmCode} AND BBID=#{bbid}

UPDATE JCPD_QDHFSHB SET SHZT=#{shzt},SHR=#{shry},SHSJ=CURRENT_DATE
,SQZT=0
WHERE BMCODE=#{bmCode} AND BBID=#{bbid}

mapper.select:
SELECT T.*,BM.BMNAME,BB.BBMC BBNAME,BB.QMBBID FROM JCPD_QDHFSHB T LEFT JOIN HTGL_BMB BM ON T.BMCODE=BM.BMCODE LEFT JOIN JCPD_BBKZB BB ON T.BBID=BB.BBID WHERE T.BBID=#{bbid} AND T.SQZT=1
AND T.BMCODE=#{bmCode}
ORDER BY SQSJ DESC

SELECT * FROM(
    SELECT ROWNUM RN,TEMP.* FROM(
	select * from jcpd_qdhfb
	where bmCode=#{bmCode} and bbid=#{bbid}
	order by lxcode,szhh
	) TEMP
	
	  	where ROWNUM <= (#{page} * #{rows})
	)

   
      RN>((#{page} - 1) * #{rows})

SELECT L.*,#{bbid} AS BBID FROM HTGL_LDB L
WHERE BMCODE=#{bmCode}
ORDER BY LDCODE
