mapper.select:
SELECT * FROM(
    SELECT ROWNUM RN,TEMP.* FROM(
		SELECT qm.*,mj.value as fxName FROM JCPD_QMLDB qm
		left join htgl_mjlx mj
		on qm.fx=mj.key
		WHERE qm.BBID=#{bbid}
		
			AND qm.LXCODE=#{lxCode}
		
		ORDER BY qm.LXCODE,qm.SZHH,qm.FX
    ) TEMP
	
	  	where ROWNUM <= (#{page} * #{rows})
	)

   
      RN>((#{page} - 1) * #{rows})

SELECT COUNT(*) FROM JCPD_QMLDB
WHERE BBID=#{bbid}

	AND LXCODE=#{lxCode}

SELECT * FROM( SELECT ROWNUM RN,TEMP.* FROM( select q.*,l.* from (select qm.*,'K'||qm.szhh||'-K'||qm.ezhh as ldName,mj.value as fxName,l.lxname from jcpd_qmldb qm left join htgl_ldb ld on ld.lxcode=qm.lxcode and qm.szhh>=ld.szhh and ld.ezhh>qm.szhh left join htgl_lxb l on qm.lxcode=l.lxcode left join htgl_mjlx mj on qm.fx=mj.key where qm.fx=#{fx} and qm.bbid=(select qmbbid from jcpd_bbkzb where bbid=#{dcbbid}) and ld.bmcode=#{bmCode}
and ld.ldcode=#{ldCode}
and qm.lmlx=#{lmlx}
)q left join (select dcid,lxid,ldid as qmldid,bbid as dcbbid,dcsj,dcry from jcpd_lkdczb where bbid=#{dcbbid} and lxid=#{lxid})l on q.ldid=l.qmldid order by q.lxname,q.szhh ) TEMP
where ROWNUM <= (#{page} * #{rows}) )
RN>((#{page} - 1) * #{rows})

SELECT COUNT(*) FROM JCPD_QMLDB QM
LEFT JOIN HTGL_LDB LD
ON QM.LXCODE=LD.LXCODE AND QM.SZHH>=LD.SZHH AND LD.EZHH>QM.SZHH
WHERE QM.FX=#{fx} AND QM.BBID=(SELECT QMBBID FROM JCPD_BBKZB WHERE BBID=#{dcbbid})
AND LD.BMCODE=#{bmCode}

	AND LD.LDCODE=#{ldCode}


	AND LMLX=#{lmlx}

select * from jcpd_lkdcfb 
where dcid=#{dcid}
order by lxid
